mapper.select:
 		SELECT COUNT(*)
		FROM (SELECT *
			  FROM ADMIN_STORE
			  WHERE APRV_STATUS=#{searchStatus}
			  
			  AND BRAND_NAME=#{searchCategory}
			  
			  
			  AND STO_NAME LIKE '%'||#{searchName}||'%'
			  )

	SELECT * FROM ADMIN_STORE
	WHERE APRV_STATUS=#{searchStatus}
	
	AND BRAND_NAME=#{searchCategory}
		

	AND STO_NAME LIKE '%'||#{searchName}||'%'

SELECT ENTER_YN
FROM STORE_INFO
WHERE STO_NAME=#{storeNum}

SELECT *
FROM STORE_LIKE
WHERE USER_ID = #{id} AND STO_NUM = #{sto_num}
