mapper.select:
SELECT COUNT(*) FROM (SELECT B.BRAND_CODE AS "BRAND_CODE", BRAND_PIC, BRAND_NAME, COUNT(*) AS "BRAND_COUNT", BRAND_YN FROM BRAND B LEFT OUTER JOIN STORE_INFO SI ON (B.BRAND_CODE = SI.BRAND_CODE) WHERE BRAND_YN=#{searchStatus}
AND BRAND_NAME LIKE '%'||#{searchName}||'%'
GROUP BY B.BRAND_CODE, BRAND_PIC, BRAND_NAME, BRAND_YN )

SELECT B.BRAND_CODE AS "BRAND_CODE", BRAND_PIC, BRAND_NAME, COUNT(*) AS "BRAND_COUNT", BRAND_YN FROM BRAND B LEFT OUTER JOIN STORE_INFO SI ON (B.BRAND_CODE = SI.BRAND_CODE) WHERE BRAND_YN=#{searchStatus}
AND BRAND_NAME LIKE '%'||#{searchName}||'%'
GROUP BY B.BRAND_CODE, BRAND_PIC, BRAND_NAME, BRAND_YN
